(()=>{const e=document.querySelector("#login-email"),o=document.querySelector("#login-password");document.querySelector("#login-button").addEventListener("click",(async()=>{console.log("login was clicked"),await fetch("http://localhost:5050/login",{method:"POST",headers:{"Content-Type":"application/json",Accept:"application/json"},body:JSON.stringify({email:e.value,password:o.value})}).then((function(e){if(e.ok)return console.log("Click was recorded"),void window.location.replace("index.html");throw new Error("Request failed.")})).catch((function(e){console.log(e)}))}));const t=()=>{const e=document.querySelector("#signup-section"),o=document.querySelector("#login-section");e.classList.contains("hidden")?(e.classList.remove("hidden"),o.classList.add("hidden")):(e.classList.add("hidden"),o.classList.remove("hidden"))};document.querySelector("#create-account").addEventListener("click",t),document.querySelector("#already-have-account").addEventListener("click",t)})();